def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context);
def holder = groovyUtils.getXmlHolder(mockRequest.getRequestContent());
holder.declareNamespace("wsc", "http://bank/schema/WSCommonTypes");

def accountNumber = holder.getNodeValue("//wsc:CheckAccountRequest/wsc:AccountNumber");
context.accNum = accountNumber;

if (accountNumber == "999999999"){ 
	context.status = "Success";
}
else if (accountNumber == "777777777"){ 
	context.status = "Blocked";
}
else if (accountNumber == "666666666"){ 
	context.status = "Error";
}
else {
	context.status = "Open";
}

//возвращает рандомное значение (для использования в ответе:         <wsc:TransactionId>${TransactionId}</wsc:TransactionId>):

context.TransactionId = Math.random()
context.TransactionId = context.TransactionId.toString().substring(5,15)

